// Reserve.js
import React, { useState, useContext, useEffect } from 'react';
import './Reserve.css';
import useFetch from '../../hooks/useFetch';
import { SearchContext } from '../../context/SearchContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Reserve = ({ setOpen, spaceId }) => {
  const [selectedTables, setSelectedTables] = useState([]);
  const { data, loading, error } = useFetch(`/spaces/table/${spaceId}`);
  const { dates } = useContext(SearchContext);
  const navigate = useNavigate();

  const getDatesInRange = (startDate, endDate) => {
    const start = new Date(startDate);
    const end = new Date(endDate);
    const date = new Date(start.getTime());

    const dates = [];

    while (date <= end) {
      dates.push(new Date(date).getTime());
      date.setDate(date.getDate() + 1);
    }

    return dates;
  };

  const alldates = getDatesInRange(dates[0].startDate, dates[0].endDate);

  const isAvailable = (chairNumber) => {
    const isFound = chairNumber.unavailableDates.some((date) =>
      alldates.includes(new Date(date).getTime())
    );

    return !isFound;
  };

  const handleSelect = (e) => {
    const checked = e.target.checked;
    const value = e.target.value;
    setSelectedTables(
      checked
        ? [...selectedTables, value]
        : selectedTables.filter((item) => item !== value)
    );
  };

  useEffect(() => {
    const handleClick = async () => {
      try {
        // Generate reservation code
        const reservationCode = generateReservationCode();

        // Update the database with the reservation code and other details
        await Promise.all(
          selectedTables.map(async (tableId) => {
            const res = await axios.put(`/tables/availability/${tableId}`, { dates: alldates, reservationCode });
            return res.data;
          })
        );

        // Navigate to another page or perform any other actions
        navigate('/ReservationKey');
      } catch (err) {
        console.error(err);
      }
    };
    handleClick();
  }, [selectedTables]); // Dependency on selectedTables to run when it changes

  // Function to generate a random numeric code of maximum length 6
  const generateReservationCode = () => {
    const min = 10000; // Minimum value for a 5-digit number
    const max = 99999; // Maximum value for a 5-digit number
    return Math.floor(Math.random() * (max - min + 1)) + min;
  };

  return (
    <div className="reserve">
      <div className="rContainer">
        <span>Select your place</span>

        {data.map((item) => (
          item && (
            <div className="rItem" key={item._id}>
              <div className="rItemInfo">
                <div className="rTitle">{item.title}</div>
                <div className="rDesc">{item.desc}</div>
                <div className="rMax">
                  Max people: <b>{item.maxPeople}</b>
                </div>
                <div className="rPrice">{item.price}</div>
              </div>
              <div className="rSelectTables">
                {item.tableNumbers.map((tableNumber) => (
                  <div className="table" key={tableNumber._id}>
                    <label>{tableNumber.number}</label>
                    <input
                      type="checkbox"
                      value={tableNumber._id}
                      onChange={handleSelect}
                      disabled={!isAvailable(tableNumber)}
                    />
                  </div>
                ))}
              </div>
            </div>
          )
        ))}
        {/* Removed handleClick from the button */}
        <button className="rButton" onClick={() => setOpen(false)}>Reserve Now!</button>
      </div>
    </div>
  );
};

export default Reserve;
