import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChair ,faPerson,faCalendarDays,  faWifi,faMugHot ,faCookieBite,faCouch,
  faCircleInfo,} from '@fortawesome/free-solid-svg-icons'
import "./Header.css";

import { DateRange } from "react-date-range";
import { useState } from "react";
import "react-date-range/dist/styles.css"; // main css file
import "react-date-range/dist/theme/default.css"; // theme css file
import { format } from "date-fns";

// import {
//   faBed,
//   faCalendarDays,
//   faCar,
//   faPerson,
//   faPlane,
//   faTaxi,
// } from "@fortawesome/free-solid-svg-icons";
const Header = () => {

  const [openDate, setOpenDate] = useState(false);
  const [date, setDate] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);
  const [openOptions, setOpenOptions] = useState(false);
  const [options, setOptions] = useState({
    adult: 1,
    children: 0,
    table: 1,
  });

  const navigate = useNavigate();

  const handleOption = (name, operation) => {
    setOptions((prev) => {
      return {
        ...prev,
        [name]: operation === "i" ? options[name] + 1 : options[name] - 1,
      };
    });
  };
    return (
      <div className="header">
        <div className="headerContainer">
        <div className="headerList">
          <div className="headerListItem active">
          <FontAwesomeIcon icon={faChair} />
            
            <span>Stays</span>
          </div>

          <div className="headerListItem">
          <FontAwesomeIcon icon={faWifi} />
           
            <span>wifi access</span>
          </div>

          <div className="headerListItem">
          <FontAwesomeIcon icon={faMugHot} />
          
            <span>Drinks</span>
          </div>
        
          <div className="headerListItem">
          <FontAwesomeIcon icon={faCookieBite} />
            <span>buy snacs</span>
          </div>
          <div className="headerListItem">
          <FontAwesomeIcon icon={faCircleInfo} />
            <span>Informations</span>
          </div>
        </div>
        <h1 className="headerTitle">
        Welcome to Elaco CoWorking Your Productive Space Awaits!
            </h1>

            <p className="headerDescreption">
            Reserve Your Seat Now and Dive into a World of Creativity and Collaboration.\<br />
            Join and get amazing discount
            </p>

            
            <button className="headerBtn">Sign in / Register</button>

            <div className="headerSearch">
            <div className="headerSearchItem">
              <FontAwesomeIcon icon={faCouch} className="headerIcon"/>
              <input
                  type="text"
                  placeholder="Where do you want to reserve?"
                  className="headerSearchInput"
                  // onChange={(e) => setDestination(e.target.value)}
                />
              </div>

              <div className="headerSearchItem">
              <FontAwesomeIcon icon={faCalendarDays} className="headerIcon"/>
           <span   onClick={() => setOpenDate(!openDate)} className="headerSearchText"> 
           {`${format(date[0].startDate, "MM/dd/yyyy")} to ${format(
                  date[0].endDate,
                  "MM/dd/yyyy"
                )}`}
                </span>
                {openDate && <DateRange 
           editableDateInputs={true}
           onChange={(item) =>setDate([item.selection])}
           moveRangeOnFirstSelection={false}
           ranges={date}
           className='date'
           />
           }
              </div>

              <div className="headerSearchItem">
              <FontAwesomeIcon icon={faPerson} className="headerIcon"/>
              <span
                  onClick={() => setOpenOptions(!openOptions)}
                  className="headerSearchText"
                >{`${options.adult} adult 路 ${options.children} children 路 ${options.table} table`}</span>             
                <div className="options">
                    <div className="optionItem">
                      <span className="optionText">Adult</span>
                      <div className="optionCounter">
                        <button className="optionCounterButton" onClick={() => handleOption("adult", "d")}>  - </button>
                        <button className="optionCounterNumber" >  1 </button>
                        <button className="optionCounterButton" onClick={() => handleOption("adult", "i")}>  + </button>
                      </div>
                    </div>

                    <div className="optionItem">
                      <span className="optionText">children</span>
                      <div className="optionCounter">
                        <button className="optionCounterButton"   onClick={() => handleOption("adult", "d")} >  - </button>
                        <button className="optionCounterNumber" >  0 </button>
                        <button className="optionCounterButton" onClick={() => handleOption("adult", "i")}>  + </button>
                      </div>
                    </div>

                    <div className="optionItem">
                      <span className="optionText">table</span>
                      <div className="optionCounter">
                        <button className="optionCounterButton" onClick={() => handleOption("adult", "d")}>  - </button>
                        <button className="optionCounterNumber" >  1</button>
                        <button className="optionCounterButton" onClick={() => handleOption("adult", "i")}>  + </button>
                      </div>
                    </div>


                </div>
                 </div> 
              <div className="headerSearchItem">
                <button className="headerBtn">search</button>
              </div> 

              </div>



        </div>
      </div>
    );
  };
  
  export default Header;
